function smoothScrollInit(){for(var e=document.querySelectorAll(".smoothscrolllink"),o=0;o<e.length;o++)e[o].addEventListener("click",function(e){e.preventDefault();var o=e.target.innerText.replace(/[\W_]/g,"").toLowerCase(),t=document.querySelector("#"+o),r=t.offsetTop;window.scrollTo({top:r,behavior:"smooth"})})}window.onload=function(){smoothScrollInit()};var hamburger=document.querySelector(".hamburger-icon");hamburger.onclick=function(){var e=document.querySelector(".main-nav"),o=document.querySelector(".language-picker"),t=document.getElementsByClassName("bar");e.classList.toggle("js-remove-top-position"),o.classList.toggle("js-remove-top-position");var r;for(r=0;r<t.length;r++)t[r].classList.toggle("open")};var pagePosition=document.pageYOffset;